import React, { useState, useCallback } from 'react';
import { useTranslation } from 'react-i18next';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Progress } from '@/components/ui/progress';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Popover, PopoverContent, PopoverTrigger } from '@/components/ui/popover';
import { Calendar } from '@/components/ui/calendar';
import { cn } from '@/lib/utils';
import { format } from 'date-fns';
import { CalendarIcon, Upload, FileText } from 'lucide-react';
import { COUNTRY_CONFIGS } from '@/config/countries';
import { useCountry } from '@/contexts/CountryContext';
import { supabase } from '@/integrations/supabase/client';
import { toast } from 'sonner';
import { MAX_FILE_SIZES } from '@/utils/constants';
import { useUserPlan } from '@/hooks/useUserPlan';

interface DocumentUploadProps {
  onDocumentUploaded: (document: Record<string, unknown>) => void;
  onCancel: () => void;
}

export const DocumentUpload: React.FC<DocumentUploadProps> = ({ onDocumentUploaded, onCancel }) => {
  const { t } = useTranslation('common');
  const { selectedCountryCode } = useCountry();

  const { plan } = useUserPlan();

  // Upload state
  const [file, setFile] = useState<File | null>(null);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);
  const [uploadedFilePath, setUploadedFilePath] = useState<string | null>(null);

  // Form state
  const [documentName, setDocumentName] = useState('');
  const [documentType, setDocumentType] = useState('');
  const [countryCode, setCountryCode] = useState(selectedCountryCode);
  const [expirationDate, setExpirationDate] = useState<Date | null>(null);

  const [showForm, setShowForm] = useState(false);

  const documentTypes = [
    { key: 'realEstateDeed', value: t('documentTypes.realEstateDeed') },
    { key: 'birthCertificate', value: t('documentTypes.birthCertificate') },
    { key: 'insurancePolicy', value: t('documentTypes.insurancePolicy') },
    { key: 'lastWill', value: t('documentTypes.lastWill') },
    { key: 'employmentContract', value: t('documentTypes.employmentContract') },
    { key: 'passport', value: t('documentTypes.passport') },
    { key: 'drivingLicense', value: t('documentTypes.drivingLicense') },
    { key: 'bankStatement', value: t('documentTypes.bankStatement') },
    { key: 'investmentCertificate', value: t('documentTypes.investmentCertificate') },
    { key: 'other', value: t('documentTypes.other') },
  ];

  const supportedCountries = Object.values(COUNTRY_CONFIGS);

  const handleFileSelect = useCallback((selectedFile: File) => {
    const maxFileSize = MAX_FILE_SIZES[plan];

    // Check file size
    if (selectedFile.size > maxFileSize) {
      const maxSizeMB = maxFileSize / (1024 * 1024);
      toast.error(t('documentUpload.fileTooLarge', { maxSize: maxSizeMB }));
      return;
    }

    setFile(selectedFile);
    if (!documentName) {
      // Auto-set document name from filename
      const nameWithoutExtension = selectedFile.name.replace(/\.[^/.]+$/, '');
      setDocumentName(nameWithoutExtension);
    }
  }, [documentName, t]);

  const handleDrop = useCallback((e: React.DragEvent) => {

    e.preventDefault();
    const droppedFile = e.dataTransfer.files[0];
    if (droppedFile) {
      handleFileSelect(droppedFile);
    }
  }, [handleFileSelect]);

  const handleDragOver = useCallback((e: React.DragEvent) => {

    e.preventDefault();
  }, []);

  const handleFileInputChange = useCallback((e: React.ChangeEvent<HTMLInputElement>) => {

    const selectedFile = e.target.files?.[0];
    if (selectedFile) {
      handleFileSelect(selectedFile);
    }
  }, [handleFileSelect]);

  const uploadFile = async () => {
    if (!file) return null;

    setIsUploading(true);
    setUploadProgress(0);

    try {
      // Create unique filename
      const fileExt = file.name.split('.').pop();
      const fileName = `${Date.now()}-${Math.random().toString(36).substring(2)}.${fileExt}`;
      const filePath = `${fileName}`;

      // Upload file to Supabase storage
      const { data, error } = await supabase.storage
        .from('documents')
        .upload(filePath, file);

      if (error) {
        throw error;
      }

      setUploadProgress(100);
      setUploadedFilePath(data.path);
      setShowForm(true);
      toast.success(t('documentUpload.fileUploadedSuccess'));

      return data.path;
    } catch (error) {
      console.error('Chyba pri nahr√°van√≠ s√∫boru:', {
        error,
        fileName: file.name,
        fileSize: file.size,
        timestamp: new Date().toISOString()
      });

      let errorMessage = t('documentUpload.fileUploadFailed');
      if (error instanceof Error) {
        if (error.message.includes('storage')) {
          errorMessage = `${t('documentUpload.fileUploadFailed')}: Probl√©m s √∫lo≈æiskom`;
        } else if (error.message.includes('network')) {
          errorMessage = `${t('documentUpload.fileUploadFailed')}: Skontrolujte internetov√© pripojenie`;
        }
      }

      toast.error(errorMessage);
      return null;
    } finally {
      setIsUploading(false);
    }
  };

  const handleSaveDocument = async () => {
    if (!uploadedFilePath || !documentName.trim() || !documentType) {
      toast.error(t('documentUpload.fillRequiredFields'));
      return;
    }

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        toast.error(t('documentUpload.loginToSave'));
        return;
      }

      const { data, error } = await supabase
        .from('documents')
        .insert({
          user_id: user.id,
          name: documentName.trim(),
          type: documentType,
          country_code: countryCode,
          file_path: uploadedFilePath,
          file_size: file?.size || 0,
          mime_type: file?.type || '',
          expiration_date: expirationDate ? format(expirationDate, 'yyyy-MM-dd') : null,
        })
        .select()
        .single();

      if (error) {
        throw error;
      }

      toast.success(t('documentUpload.saved'));
      onDocumentUploaded(data);
    } catch (error) {
      console.error('Chyba pri ukladan√≠ dokumentu:', {
        error,
        documentName: documentName.trim(),
        documentType,
        timestamp: new Date().toISOString()
      });

      let errorMessage = t('documentUpload.failedSave');
      if (error instanceof Error) {
        if (error.message.includes('duplicate')) {
          errorMessage = `${t('documentUpload.failedSave')}: Dokument s t√Ωmto n√°zvom u≈æ existuje`;
        } else if (error.message.includes('permission')) {
          errorMessage = `${t('documentUpload.failedSave')}: Nedostatoƒçn√© opr√°vnenia`;
        }
      }

      toast.error(errorMessage);
    }
  };

  if (showForm) {
    return (
      <div className="space-y-6">
        <div className="text-center">
          <FileText className="mx-auto h-12 w-12 text-primary mb-4" />
          <h3 className="text-lg font-semibold">{t('documentUpload.detailsTitle')}</h3>
          <p className="text-sm text-muted-foreground">{t('documentUpload.detailsSubtitle')}</p>
        </div>

        <div className="space-y-4">
          <div className="space-y-2">
            <Label htmlFor="documentName">{t('dashboard.documentName')}</Label>
            <Input
              id="documentName"
              placeholder={t('documentUpload.namePlaceholder')}
              value={documentName}
              onChange={(e) => setDocumentName(e.target.value)}
            />
          </div>

          <div className="space-y-2">
            <Label htmlFor="documentType">{t('dashboard.documentType')}</Label>
            <Select value={documentType} onValueChange={setDocumentType}>
              <SelectTrigger>
                <SelectValue placeholder={t('documentUpload.typePlaceholder')} />
              </SelectTrigger>
              <SelectContent>
                {documentTypes.map((type) => (
                  <SelectItem key={type.key} value={type.value}>
                    {type.value}
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label htmlFor="countryCode">{t('dashboard.countryOfRelevance')}</Label>
            <Select value={countryCode} onValueChange={setCountryCode}>
              <SelectTrigger>
                <SelectValue placeholder={t('documentUpload.countryPlaceholder')} />
              </SelectTrigger>
              <SelectContent>
                {supportedCountries.map((country) => (
                  <SelectItem key={country.code} value={country.code}>
                    <div className="flex items-center gap-2">
                      <span>{country.flag}</span>
                      <span>{country.name}</span>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
          </div>

          <div className="space-y-2">
            <Label>{t('dashboard.expirationDate')}</Label>
            <Popover>
              <PopoverTrigger asChild>
                <Button
                  variant="outline"
                  className={cn(
                    "w-full justify-start text-left font-normal",
                    !expirationDate && "text-muted-foreground"
                  )}
                >
                  <CalendarIcon className="mr-2 h-4 w-4" />
                  {expirationDate ? format(expirationDate, "PPP") : <span>{t('documentUpload.pickDate')}</span>}
                </Button>
              </PopoverTrigger>
              <PopoverContent className="w-auto p-0" align="start">
                <Calendar
                  mode="single"
                  selected={expirationDate}
                  onSelect={setExpirationDate}
                  initialFocus
                  className={cn("p-3 pointer-events-auto")}
                />
              </PopoverContent>
            </Popover>
          </div>
        </div>

        <div className="flex gap-3 pt-4">
          <Button variant="outline" onClick={onCancel} className="flex-1">
            {t('common.cancel')}
          </Button>
          <Button onClick={handleSaveDocument} className="flex-1">
            {t('dashboard.saveDocument')}
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="text-center">
        <Upload className="mx-auto h-12 w-12 text-primary mb-4" />
        <h3 className="text-lg font-semibold">{t('documentUpload.title')}</h3>
        <p className="text-sm text-muted-foreground">{t('documentUpload.subtitle')}</p>
      </div>

      {!file && (
        <div
          className="border-2 border-dashed border-primary/20 rounded-lg p-8 text-center cursor-pointer hover:border-primary/40 transition-colors"
          onDrop={handleDrop}
          onDragOver={handleDragOver}
          onClick={() => document.getElementById('file-input')?.click()}
        >
          <Upload className="mx-auto h-8 w-8 text-muted-foreground mb-4" />
          <p className="text-sm text-muted-foreground mb-2">
            {t('dashboard.dragDropZone')}
          </p>
          <Button variant="outline" size="sm">
            {t('documentUpload.browseFiles')}
          </Button>
          <input
            id="file-input"
            type="file"
            className="hidden"
            onChange={handleFileInputChange}
            accept=".pdf,.doc,.docx,.jpg,.jpeg,.png,.txt"
          />
        </div>
      )}

      {file && !isUploading && !uploadedFilePath && (
        <div className="space-y-4">
          <div className="flex items-center gap-3 p-3 border rounded-lg">
            <FileText className="h-8 w-8 text-primary" />
            <div className="flex-1">
              <p className="font-medium text-sm">{file.name}</p>
              <p className="text-xs text-muted-foreground">
                {(file.size / 1024 / 1024).toFixed(2)} MB
              </p>
            </div>
          </div>

          <div className="flex gap-3">
            <Button variant="outline" onClick={() => setFile(null)} className="flex-1">
              {t('documentUpload.chooseDifferent')}
            </Button>
            <Button onClick={uploadFile} className="flex-1">
              {t('documentUpload.uploadButton')}
            </Button>
          </div>
        </div>
      )}

      {isUploading && (
        <div className="space-y-4">
          <div className="text-center">
            <p className="text-sm font-medium">{t('dashboard.fileUploading')}</p>
            <p className="text-xs text-muted-foreground">{file?.name}</p>
          </div>
          <Progress value={uploadProgress} className="w-full" />
        </div>
      )}
    </div>
  );
};

import React, { createContext, useContext, useState, ReactNode } from 'react';
import { getCurrentCountryConfig } from '@/config/countries';

interface CountryContextType {
  selectedCountryCode: string;
  setSelectedCountryCode: (countryCode: string) => void;
}

const CountryContext = createContext<CountryContextType | undefined>(undefined);

export const useCountry = () => {
  const context = useContext(CountryContext);
  if (context === undefined) {
    throw new Error('useCountry must be used within a CountryProvider');
  }
  return context;
};

interface CountryProviderProps {
  children: ReactNode;
}

export const CountryProvider: React.FC<CountryProviderProps> = ({ children }) => {
  // Get the initial country code safely - defer to useEffect for safety
  const [selectedCountryCode, setSelectedCountryCode] = useState('DE');

  // Use effect to set the correct country based on domain
  React.useEffect(() => {

    try {
      const currentConfig = getCurrentCountryConfig();
      setSelectedCountryCode(currentConfig.code);
    } catch (error) {
      // Failed to get country config, using default
      setSelectedCountryCode('DE');
    }
  }, []);

  return (
    <CountryContext.Provider value={{ selectedCountryCode, setSelectedCountryCode }}>
      {children}
    </CountryContext.Provider>
  );
};

import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, useSearchParams } from 'react-router-dom';
import { useTranslation } from 'react-i18next';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { CountrySelector } from '@/components/common/CountrySelector';
import { useCountry } from '@/contexts/CountryContext';
import { supabase } from '@/integrations/supabase/client';
import { toast } from 'sonner';
import { ArrowLeft, Save, Home, Building, Car, Wallet } from 'lucide-react';

interface Asset {
  id: string;
  user_id: string;
  name: string;
  type: string;
  address?: string;
  property_registry_number?: string;
  estimated_value?: number;
  currency_code: string;
  metadata: Record<string, unknown>;
}

const CURRENCIES = [
  { code: 'EUR', symbol: '‚Ç¨', name: 'Euro' },
  { code: 'USD', symbol: '$', name: 'US Dollar' },
  { code: 'GBP', symbol: '¬£', name: 'British Pound' },
  { code: 'CZK', symbol: 'Kƒç', name: 'Czech Koruna' },
  { code: 'PLN', symbol: 'z≈Ç', name: 'Polish Z≈Çoty' },
];

export const AssetDetail: React.FC = () => {
  const { t } = useTranslation('common');
  const { assetId } = useParams<{ assetId: string }>();
  const [searchParams] = useSearchParams();
  const navigate = useNavigate();
  const { selectedCountryCode } = useCountry();

  const [isLoading, setIsLoading] = useState(false);
  const [isSaving, setIsSaving] = useState(false);
  const [asset, setAsset] = useState<Asset | null>(null);

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    type: searchParams.get('type') || 'RealEstate',
    address: '',
    property_registry_number: '',
    estimated_value: '',
    currency_code: 'EUR',
    // Financial Account specific fields
    account_type: '',
    financial_institution: '',
    account_number: '',
    login_credentials: '',
  });

  const isNewAsset = assetId === 'new';

  useEffect(() => {

    if (!isNewAsset && assetId) {
      loadAsset();
    }
  }, [assetId, isNewAsset]);

  const loadAsset = async () => {
    if (!assetId) return;

    setIsLoading(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        navigate('/login');
        return;
      }

      const { data, error } = await supabase
        .from('assets')
        .select('*')
        .eq('id', assetId)
        .eq('user_id', user.id)
        .single();

      if (error) {
        throw error;
      }

      setAsset(data);
      setFormData({
        name: data.name || '',
        type: data.type || 'RealEstate',
        address: data.address || '',
        property_registry_number: data.property_registry_number || '',
        estimated_value: data.estimated_value?.toString() || '',
        currency_code: data.currency_code || 'EUR',
        // Financial Account specific fields
        account_type: (data.metadata as Record<string, unknown>)?.account_type || '',
        financial_institution: (data.metadata as Record<string, unknown>)?.financial_institution || '',
        account_number: (data.metadata as Record<string, unknown>)?.account_number || '',
        login_credentials: (data.metadata as Record<string, unknown>)?.login_credentials || '',
      });
    } catch (error) {
      toast.error(t('assetDetail.errors.failedToLoad'));
      navigate('/dashboard');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    if (!formData.name.trim()) {
      toast.error(t('assetDetail.errors.nameRequired'));
      return;
    }

    setIsSaving(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        navigate('/login');
        return;
      }

      const assetData = {
        user_id: user.id,
        name: formData.name.trim(),
        type: formData.type,
        address: formData.address.trim() || null,
        property_registry_number: formData.property_registry_number.trim() || null,
        estimated_value: formData.estimated_value ? parseFloat(formData.estimated_value) : null,
        currency_code: formData.currency_code,
        metadata: formData.type === 'FinancialAccount' ? {
          account_type: formData.account_type,
          financial_institution: formData.financial_institution,
          account_number: formData.account_number,
          login_credentials: formData.login_credentials,
        } : {},
      };

      if (isNewAsset) {
        const { error } = await supabase
          .from('assets')
          .insert([assetData]);

        if (error) throw error;

        toast.success(t('assetDetail.messages.createdSuccessfully'));
      } else {
        const { error } = await supabase
          .from('assets')
          .update(assetData)
          .eq('id', assetId)
          .eq('user_id', user.id);

        if (error) throw error;

        toast.success(t('assetDetail.messages.updatedSuccessfully'));
      }

      navigate('/dashboard');
    } catch (error) {
      toast.error(t('assetDetail.errors.failedToSave'));
    } finally {
      setIsSaving(false);
    }
  };

  const getAssetIcon = (type: string) => {
    switch (type) {
      case 'RealEstate':
        return <Home className="h-6 w-6" />;
      case 'Business':
        return <Building className="h-6 w-6" />;
      case 'Vehicle':
        return <Car className="h-6 w-6" />;
      case 'FinancialAccount':
        return <Wallet className="h-6 w-6" />;
      default:
        return <Home className="h-6 w-6" />;
    }
  };

  const getAssetTypeLabel = (type: string) => {
    switch (type) {
      case 'RealEstate':
        return t('assetDetail.types.realEstate');
      case 'Business':
        return t('assetDetail.types.business');
      case 'Vehicle':
        return t('assetDetail.types.vehicle');
      case 'FinancialAccount':
        return t('assetDetail.types.financialAccount');
      default:
        return t('assetDetail.types.asset');
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto px-4 lg:px-8 py-8">
        <div className="flex items-center justify-center min-h-[400px]">
          <div className="text-center">
            <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
            <p className="text-muted-foreground">{t('assetDetail.loading')}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 lg:px-8 py-8 space-y-6">
      {/* Header */}
      <div className="flex items-center gap-4">
        <Button
          variant="outline"
          size="icon"
          onClick={() => navigate('/dashboard')}
        >
          <ArrowLeft className="h-4 w-4" />
        </Button>
        <div className="flex items-center gap-3">
          {getAssetIcon(formData.type)}
          <div>
            <h1 className="text-2xl font-bold">
              {isNewAsset ? t('assetDetail.title.new', { type: getAssetTypeLabel(formData.type) }) : formData.name}
            </h1>
            <p className="text-muted-foreground">
              {isNewAsset ? t('assetDetail.subtitle.new') : t('assetDetail.subtitle.edit')}
            </p>
          </div>
        </div>
      </div>

      {/* Asset Form */}
      <Card>
        <CardHeader>
          <CardTitle>{t('assetDetail.sections.assetInformation')}</CardTitle>
        </CardHeader>
        <CardContent className="space-y-8">
          {/* Basic Information Section */}
          <div className="space-y-4">
            <h3 className="text-lg font-semibold border-b pb-2">{t('assetDetail.sections.basicInformation')}</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="space-y-2">
                <Label htmlFor="name">{t('assetDetail.fields.assetName')} *</Label>
                <Input
                  id="name"
                  placeholder={t('assetDetail.placeholders.assetName')}
                  value={formData.name}
                  onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                />
                <p className="text-xs text-muted-foreground">
                  {t('assetDetail.descriptions.assetName')}
                </p>
              </div>

              <div className="space-y-2">
                <Label htmlFor="type">{t('assetDetail.fields.assetType')}</Label>
                <Select
                  value={formData.type}
                  onValueChange={(value) => setFormData(prev => ({ ...prev, type: value }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="RealEstate">{t('assetDetail.types.realEstate')}</SelectItem>
                    <SelectItem value="Business">{t('assetDetail.types.business')}</SelectItem>
                    <SelectItem value="Vehicle">{t('assetDetail.types.vehicle')}</SelectItem>
                    <SelectItem value="FinancialAccount">{t('assetDetail.types.financialAccount')}</SelectItem>
                  </SelectContent>
                </Select>
                <p className="text-xs text-muted-foreground">
                  {t('assetDetail.descriptions.assetType')}
                </p>
              </div>
            </div>
          </div>

          {/* Real Estate Specific Fields */}
          {formData.type === 'RealEstate' && (
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">{t('assetDetail.sections.propertyDetails')}</h3>
              <div className="space-y-4">
                <div className="space-y-2">
                  <Label htmlFor="address">{t('assetDetail.fields.propertyAddress')}</Label>
                  <Textarea
                    id="address"
                    placeholder={t('assetDetail.placeholders.propertyAddress')}
                    value={formData.address}
                    onChange={(e) => setFormData(prev => ({ ...prev, address: e.target.value }))}
                    rows={3}
                  />
                  <p className="text-xs text-muted-foreground">
                    {t('assetDetail.descriptions.propertyAddress')}
                  </p>
                </div>

                <div className="space-y-2">
                  <Label htmlFor="registry">{t('assetDetail.fields.propertyRegistryNumber')}</Label>
                  <Input
                    id="registry"
                    placeholder={t('assetDetail.placeholders.propertyRegistryNumber')}
                    value={formData.property_registry_number}
                    onChange={(e) => setFormData(prev => ({ ...prev, property_registry_number: e.target.value }))}
                  />
                  <p className="text-xs text-muted-foreground">
                    {t('assetDetail.descriptions.propertyRegistryNumber')}
                  </p>
                </div>
              </div>
            </div>
          )}

          {/* Financial Account Specific Fields */}
          {formData.type === 'FinancialAccount' && (
            <div className="space-y-4">
              <h3 className="text-lg font-semibold border-b pb-2">{t('assetDetail.sections.accountDetails')}</h3>
              <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                <div className="space-y-2">
                  <Label htmlFor="accountType">{t('assetDetail.fields.accountType')}</Label>
                  <Select
                    value={formData.account_type}
