  const [documents, setDocuments] = useState<Document[]>([]);
  const [instructions, setInstructions] = useState<Instructions | null>(null);
  const [isContactSheetOpen, setIsContactSheetOpen] = useState(false);
  const [editingContact, setEditingContact] = useState<Contact | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [isDocumentsLoading, setIsDocumentsLoading] = useState(true);
  const [isInstructionsLoading, setIsInstructionsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);

  // Form state
  const [formData, setFormData] = useState({
    name: '',
    role: '',
    phone_number: '',
    email: '',
    notes: '',
  });

  useEffect(() => {

    loadContacts();
    loadKeyDocuments();
    loadInstructions();
  }, []);

  const loadContacts = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('contacts')
        .select('*')
        .eq('user_id', user.id)
        .order('created_at', { ascending: false });

      if (error) {
        throw error;
      }

      setContacts(data || []);
    } catch (error) {
      toast.error(t('manual.contacts.errors.loadFailed'));
    } finally {
      setIsLoading(false);
    }
  };

  const resetForm = () => {
    setFormData({
      name: '',
      role: '',
      phone_number: '',
      email: '',
      notes: '',
    });
    setEditingContact(null);
  };

  const handleAddContact = () => {
    resetForm();
    setIsContactSheetOpen(true);
  };

  const handleEditContact = (contact: Contact) => {
    setEditingContact(contact);
    setFormData({
      name: contact.name,
      role: contact.role,
      phone_number: contact.phone_number || '',
      email: contact.email || '',
      notes: contact.notes || '',
    });
    setIsContactSheetOpen(true);
  };

  const handleSaveContact = async () => {
    if (!formData.name.trim() || !formData.role.trim()) {
      toast.error(t('manual.contacts.messages.nameAndRoleRequired'));
      return;
    }

    setIsSaving(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        toast.error(t('manual.contacts.messages.loginRequired'));
        return;
      }

      const contactData = {
        user_id: user.id,
        name: formData.name.trim(),
        role: formData.role.trim(),
        phone_number: formData.phone_number.trim() || null,
        email: formData.email.trim() || null,
        notes: formData.notes.trim() || null,
      };

      if (editingContact) {
        const { error } = await supabase
          .from('contacts')
          .update(contactData)
          .eq('id', editingContact.id)
          .eq('user_id', user.id);

        if (error) throw error;
        toast.success(t('manual.contacts.messages.updated'));
      } else {
        const { error } = await supabase
          .from('contacts')
          .insert([contactData]);

        if (error) throw error;
        toast.success(t('manual.contacts.messages.added'));
      }

      setIsContactSheetOpen(false);
      resetForm();
      loadContacts();
    } catch (error) {
      toast.error(t('manual.contacts.errors.saveFailed'));
    } finally {
      setIsSaving(false);
    }
  };

  const handleDeleteContact = async (id: string) => {
    try {
      const { error } = await supabase
        .from('contacts')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setContacts(prev => prev.filter(contact => contact.id !== id));
      toast.success(t('manual.contacts.messages.deleted'));
    } catch (error) {
      toast.error(t('manual.contacts.errors.deleteFailed'));
    }
  };

  const loadKeyDocuments = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('documents')
        .select('*')
        .eq('user_id', user.id)
        .eq('is_key_document', true)
        .order('created_at', { ascending: false });

      if (error) {
        throw error;
      }

      setDocuments(data || []);
    } catch (error) {
      toast.error(t('manual.documents.errors.loadFailed'));
    } finally {
      setIsDocumentsLoading(false);
    }
  };

  const getCountryFlag = (countryCode: string) => {
    // Simple flag mapping - in a real app you'd want a more comprehensive solution
    const flags: { [key: string]: string } = {
      'SK': 'üá∏üá∞', 'CZ': 'üá®üáø', 'DE': 'üá©üá™', 'AT': 'üá¶üáπ', 'HU': 'üá≠üá∫',
      'PL': 'üáµüá±', 'IT': 'üáÆüáπ', 'FR': 'üá´üá∑', 'ES': 'üá™üá∏', 'NL': 'üá≥üá±',
      'BE': 'üáßüá™', 'CH': 'üá®üá≠', 'US': 'üá∫üá∏', 'GB': 'üá¨üáß', 'IE': 'üáÆüá™'
    };
    return flags[countryCode] || 'üè≥Ô∏è';
  };

  const loadInstructions = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('instructions')
        .select('*')
        .eq('user_id', user.id)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      setInstructions(data);
    } catch (error) {
      console.error('Error loading instructions:', error);
    } finally {
      setIsInstructionsLoading(false);
    }
  };

  const debouncedSaveInstructions = useCallback(
    async (field: keyof Instructions, value: string) => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) return;

        const instructionsData = {
          user_id: user.id,
          [field]: value.trim() || null,
        };

        if (instructions) {
          const { error } = await supabase
            .from('instructions')
            .update(instructionsData)
            .eq('id', instructions.id);

          if (error) throw error;
        } else {
          const { data, error } = await supabase
            .from('instructions')
            .insert([instructionsData])
            .select()
            .single();

          if (error) throw error;
          setInstructions(data);
        }
      } catch (error) {
    console.error("Error occurred:", error);
  }
    },
    [instructions]
  );

  const getExpirationStatus = (expirationDate: string | null) => {
    if (!expirationDate) return null;

    const today = new Date();
    const expiry = new Date(expirationDate);
    const diffTime = expiry.getTime() - today.getTime();
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays < 0) {
      return { text: t('manual.documents.expiration.expired'), variant: 'destructive' as const };
    } else if (diffDays <= 30) {
      return { text: t('manual.documents.expiration.expiresSoon'), variant: 'destructive' as const };
    } else if (diffDays <= 90) {
      return { text: t('manual.documents.expiration.expiring'), variant: 'secondary' as const };
    }
    return null;
  };

  return (
    <div className="container mx-auto px-4 lg:px-8 py-8 space-y-6">
      {/* Header */}
      <div className="flex items-center gap-3">
        <BookUser className="h-8 w-8 text-primary" />
        <div>
          <h1 className="text-3xl font-bold">{t('manual.title')}</h1>
          <p className="text-muted-foreground">
            {t('manual.subtitle')}
          </p>
        </div>
      </div>

      {/* Main Content */}
      <Tabs defaultValue="contacts" className="space-y-6">
        <TabsList className="grid w-full grid-cols-3">
          <TabsTrigger value="contacts">{t('manual.tabs.contacts')}</TabsTrigger>
          <TabsTrigger value="documents">{t('manual.tabs.documents')}</TabsTrigger>
          <TabsTrigger value="instructions">{t('manual.tabs.instructions')}</TabsTrigger>
        </TabsList>

        <TabsContent value="contacts" className="space-y-6">
          {/* Add Contact Button */}
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-xl font-semibold">{t('manual.contacts.title')}</h2>
              <p className="text-muted-foreground">
                {t('manual.contacts.description')}
              </p>
            </div>
            <Sheet open={isContactSheetOpen} onOpenChange={setIsContactSheetOpen}>
              <SheetTrigger asChild>
                <Button onClick={handleAddContact}>
                  <Plus className="mr-2 h-4 w-4" />
                  {t('manual.contacts.addContact')}
                </Button>
              </SheetTrigger>
              <SheetContent>
                <SheetHeader>
                  <SheetTitle>
                    {editingContact ? t('manual.contacts.form.editTitle') : t('manual.contacts.form.addTitle')}
                  </SheetTitle>
                  <SheetDescription>
                    {t('manual.contacts.form.description')}
                  </SheetDescription>
                </SheetHeader>

                <div className="space-y-4 mt-6">
                  <div className="space-y-2">
                    <Label htmlFor="contactName">{t('manual.contacts.form.nameLabel')}</Label>
                    <Input
                      id="contactName"
                      placeholder={t('manual.contacts.form.namePlaceholder')}
                      value={formData.name}
                      onChange={(e) => setFormData(prev => ({ ...prev, name: e.target.value }))}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="role">{t('manual.contacts.form.roleLabel')}</Label>
                    <Input
                      id="role"
                      placeholder={t('manual.contacts.form.rolePlaceholder')}
                      value={formData.role}
                      onChange={(e) => setFormData(prev => ({ ...prev, role: e.target.value }))}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="phone">{t('manual.contacts.form.phoneLabel')}</Label>
                    <Input
                      id="phone"
                      placeholder={t('manual.contacts.form.phonePlaceholder')}
                      value={formData.phone_number}
                      onChange={(e) => setFormData(prev => ({ ...prev, phone_number: e.target.value }))}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="email">{t('manual.contacts.form.emailLabel')}</Label>
                    <Input
                      id="email"
                      type="email"
                      placeholder={t('manual.contacts.form.emailPlaceholder')}
                      value={formData.email}
                      onChange={(e) => setFormData(prev => ({ ...prev, email: e.target.value }))}
                    />
                  </div>

                  <div className="space-y-2">
                    <Label htmlFor="notes">{t('manual.contacts.form.notesLabel')}</Label>
                    <Textarea
                      id="notes"
                      placeholder={t('manual.contacts.form.notesPlaceholder')}
                      value={formData.notes}
                      onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                      rows={3}
                    />
                  </div>

                  <div className="flex gap-3 pt-4">
                    <Button
                      variant="outline"
                      onClick={() => setIsContactSheetOpen(false)}
                      className="flex-1"
                      disabled={isSaving}
                    >
                      {t('manual.contacts.form.cancel')}
                    </Button>
                    <Button
                      onClick={handleSaveContact}
                      className="flex-1"
                      disabled={isSaving}
                    >
                      {isSaving ? (
                        <>
                          <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                          {t('manual.contacts.form.saving')}
                        </>
                      ) : (
                        editingContact ? t('manual.contacts.form.updateButton') : t('manual.contacts.form.addButton')
                      )}
                    </Button>
                  </div>
                </div>
              </SheetContent>
            </Sheet>
          </div>

          {/* Contacts List */}
          {isLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                <p className="text-muted-foreground">{t('manual.contacts.loading')}</p>
              </div>
            </div>
          ) : contacts.length === 0 ? (
            <Card>
              <CardContent className="py-12 text-center">
                <User className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold mb-2">{t('manual.contacts.empty.title')}</h3>
                <p className="text-muted-foreground mb-4">
                  {t('manual.contacts.empty.description')}
                </p>
                <Button onClick={handleAddContact}>
                  <Plus className="mr-2 h-4 w-4" />
                  {t('manual.contacts.addFirstContact')}
                </Button>
              </CardContent>
            </Card>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {contacts.map((contact) => (
                <Card key={contact.id} className="hover:shadow-lg transition-shadow">
                  <CardHeader className="pb-3">
                    <div className="flex items-start justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                          <User className="h-5 w-5 text-primary" />
                        </div>
                        <div>
                          <CardTitle className="text-lg">{contact.name}</CardTitle>
                          <Badge variant="secondary" className="text-xs">
                            {contact.role}
                          </Badge>
                        </div>
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    {contact.phone_number && (
                      <div className="flex items-center gap-2 text-sm">
                        <Phone className="h-4 w-4 text-muted-foreground" />
                        <span>{contact.phone_number}</span>
                      </div>
                    )}
                    {contact.email && (
                      <div className="flex items-center gap-2 text-sm">
                        <Mail className="h-4 w-4 text-muted-foreground" />
                        <span className="truncate">{contact.email}</span>
                      </div>
                    )}
                    {contact.notes && (
                      <p className="text-sm text-muted-foreground line-clamp-2">
                        {contact.notes}
                      </p>
                    )}

                    <div className="flex gap-2 pt-2">
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleEditContact(contact)}
                        className="flex-1"
                      >
                        <Edit className="mr-1 h-3 w-3" />
                        {t('manual.contacts.card.edit')}
                      </Button>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleDeleteContact(contact.id)}
                        className="text-destructive hover:text-destructive"
                      >
                        <Trash2 className="h-3 w-3" />
                      </Button>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="documents" className="space-y-6">
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-xl font-semibold">{t('manual.documents.title')}</h2>
              <p className="text-muted-foreground">
                {t('manual.documents.description')}
              </p>
            </div>
          </div>

          {/* Key Documents List */}
          {isDocumentsLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                <p className="text-muted-foreground">{t('manual.documents.loading')}</p>
              </div>
            </div>
          ) : documents.length === 0 ? (
            <Card>
              <CardContent className="py-12 text-center">
                <FileText className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                <h3 className="text-lg font-semibold mb-2">{t('manual.documents.empty.title')}</h3>
                <p className="text-muted-foreground mb-4">
                  {t('manual.documents.empty.description')}
                </p>
                <Badge variant="outline" className="text-xs">
                  <Star className="mr-1 h-3 w-3" />
                  {t('manual.documents.empty.tip')}
                </Badge>
              </CardContent>
            </Card>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
              {documents.map((document) => (
                <Card key={document.id} className="hover:shadow-lg transition-shadow">
                  <CardHeader className="pb-3">
                    <div className="flex items-start justify-between">
                      <div className="flex items-center gap-3">
                        <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                          <FileText className="h-5 w-5 text-primary" />
                        </div>
                        <div>
                          <CardTitle className="text-lg">{document.name}</CardTitle>
                          <Badge variant="secondary" className="text-xs">
                            {document.type}
                          </Badge>
                        </div>
                      </div>
                      <div className="flex items-center gap-2">
                        <span className="text-lg">{getCountryFlag(document.country_code)}</span>
                        <Star className="h-4 w-4 text-yellow-500 fill-current" />
                      </div>
                    </div>
                  </CardHeader>
                  <CardContent>
                    {getExpirationStatus(document.expiration_date) && (
                      <Badge 
                        variant={getExpirationStatus(document.expiration_date)!.variant}
                        className="text-xs mb-2"
                      >
                        {getExpirationStatus(document.expiration_date)!.text}
                      </Badge>
                    )}
                    <p className="text-xs text-muted-foreground">
                      {t('manual.documents.card.essentialNote')}
                    </p>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </TabsContent>

        <TabsContent value="instructions" className="space-y-6">
          <div className="flex justify-between items-center">
            <div>
              <h2 className="text-xl font-semibold">{t('manual.instructions.title')}</h2>
              <p className="text-muted-foreground">
                {t('manual.instructions.description')}
              </p>
            </div>
          </div>

          {isInstructionsLoading ? (
            <div className="flex items-center justify-center py-12">
              <div className="text-center">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
                <p className="text-muted-foreground">{t('manual.instructions.loading')}</p>
              </div>
            </div>
          ) : (
            <div className="space-y-6">
              {/* Funeral Wishes */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Heart className="h-5 w-5 text-primary" />
                    {t('manual.instructions.funeral.title')}
                  </CardTitle>
                  <CardDescription>
                    {t('manual.instructions.funeral.description')}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Textarea
                    placeholder={t('manual.instructions.funeral.placeholder')}
                    value={instructions?.funeral_wishes || ''}
                    onChange={(e) => {
                      const value = e.target.value;
                      setInstructions(prev => prev ? { ...prev, funeral_wishes: value } : { id: '', funeral_wishes: value });
                      debouncedSaveInstructions('funeral_wishes', value);
                    }}
                    rows={4}
                    className="resize-none"
                  />
                </CardContent>
              </Card>

              {/* Digital Accounts */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <Trash className="h-5 w-5 text-primary" />
                    {t('manual.instructions.digitalAccounts.title')}
                  </CardTitle>
                  <CardDescription>
                    {t('manual.instructions.digitalAccounts.description')}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Textarea
                    placeholder={t('manual.instructions.digitalAccounts.placeholder')}
                    value={instructions?.digital_accounts_shutdown || ''}
                    onChange={(e) => {
                      const value = e.target.value;
                      setInstructions(prev => prev ? { ...prev, digital_accounts_shutdown: value } : { id: '', digital_accounts_shutdown: value });
                      debouncedSaveInstructions('digital_accounts_shutdown', value);
                    }}
                    rows={4}
                    className="resize-none"
                  />
                </CardContent>
              </Card>

              {/* Messages to Loved Ones */}
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center gap-2">
                    <MessageSquare className="h-5 w-5 text-primary" />
                    {t('manual.instructions.messages.title')}
                  </CardTitle>
                  <CardDescription>
                    {t('manual.instructions.messages.description')}
                  </CardDescription>
                </CardHeader>
                <CardContent>
                  <Textarea
                    placeholder={t('manual.instructions.messages.placeholder')}
                    value={instructions?.messages_to_loved_ones || ''}
                    onChange={(e) => {
                      const value = e.target.value;
                      setInstructions(prev => prev ? { ...prev, messages_to_loved_ones: value } : { id: '', messages_to_loved_ones: value });
                      debouncedSaveInstructions('messages_to_loved_ones', value);
                    }}
                    rows={6}
                    className="resize-none"
                  />
                </CardContent>
              </Card>

              <div className="text-center py-4">
                <Badge variant="outline" className="text-xs">
                  <Star className="mr-1 h-3 w-3" />
                  {t('manual.instructions.autoSaveNote')}
                </Badge>
              </div>
            </div>
          )}
        </TabsContent>
      </Tabs>
    </div>
  );
};

import React, { useState, useEffect } from 'react';
import { useGenderAwareTranslation } from '@/i18n/useGenderAwareTranslation';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Badge } from '@/components/ui/badge';
import { supabase } from '@/integrations/supabase/client';
import { toast } from 'sonner';
import { FileSignature, Upload, MapPin, User, Save } from 'lucide-react';
import { DocumentUpload } from '@/components/dashboard/DocumentUpload';

interface Will {
  id: string;
  status: string;
  document_path?: string;
  physical_location?: string;
  executor_contact_id?: string;
  notes?: string;
}

interface Contact {
  id: string;
  name: string;
  role: string;
}

export const Will: React.FC = () => {
  const { t } = useGenderAwareTranslation('common');
  const [will, setWill] = useState<Will | null>(null);
  const [contacts, setContacts] = useState<Contact[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isSaving, setIsSaving] = useState(false);
  const [showUpload, setShowUpload] = useState(false);

  const [formData, setFormData] = useState({
    status: 'notStarted', // Use key instead of translated value
    physical_location: '',
    executor_contact_id: '',
    notes: '',
  });

  const statusOptions = [
    { key: 'notStarted', label: t('will.status.notStarted') },
    { key: 'inPreparation', label: t('will.status.inPreparation') },
    { key: 'completedSigned', label: t('will.status.completedSigned') },
    { key: 'withNotary', label: t('will.status.withNotary') }
  ];

  const getStatusVariant = (status: string) => {
    switch (status) {
      case 'notStarted': return 'secondary';
      case 'inPreparation': return 'default';
      case 'completedSigned': return 'default';
      case 'withNotary': return 'default';
      default: return 'secondary';
    }
  };

  useEffect(() => {

    loadWill();
    loadContacts();
  }, []);

  const loadWill = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('wills')
        .select('*')
        .eq('user_id', user.id)
        .single();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setWill(data);
        setFormData({
          status: data.status,
          physical_location: data.physical_location || '',
          executor_contact_id: data.executor_contact_id || '',
          notes: data.notes || '',
        });
      }
    } catch (error) {
      toast.error(t('will.errors.failedToLoad'));
    } finally {
      setIsLoading(false);
    }
  };

  const loadContacts = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const { data, error } = await supabase
        .from('contacts')
        .select('id, name, role')
        .eq('user_id', user.id)
        .order('name');

      if (error) throw error;
      setContacts(data || []);
    } catch (error) {
      // Error loading contacts
    }
  };

  const handleSave = async () => {
    setIsSaving(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) {
        toast.error(t('will.errors.mustBeLoggedIn'));
        return;
      }

      const willData = {
        user_id: user.id,
        status: formData.status,
        physical_location: formData.physical_location.trim() || null,
        executor_contact_id: formData.executor_contact_id || null,
        notes: formData.notes.trim() || null,
      };

      if (will) {
        const { error } = await supabase
          .from('wills')
          .update(willData)
          .eq('id', will.id);

        if (error) throw error;
      } else {
        const { data, error } = await supabase
          .from('wills')
          .insert([willData])
          .select()
          .single();

        if (error) throw error;
        setWill(data);
      }

      toast.success(t('will.messages.savedSuccessfully'));
      loadWill();
    } catch (error) {
      toast.error(t('will.errors.failedToSave'));
    } finally {
      setIsSaving(false);
    }
  };

  const handleDocumentUploaded = (document: Record<string, unknown>) => {
    setShowUpload(false);
    loadWill();
    toast.success(t('will.messages.documentUploaded'));
  };

  if (isLoading) {
    return (
      <div className="flex items-center justify-center py-12">
        <div className="text-center">
          <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-primary mx-auto mb-4"></div>
          <p className="text-muted-foreground">{t('will.loading')}</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 lg:px-8 py-8 space-y-6">
      {/* Header */}
      <div className="flex items-center gap-3">
        <FileSignature className="h-8 w-8 text-primary" />
        <div>
          <h1 className="text-3xl font-bold">{t('will.title')}</h1>
          <p className="text-muted-foreground">
            {t('will.subtitle')}
          </p>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Status & Basic Information */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <FileSignature className="h-5 w-5" />
              {t('will.sections.statusInfo')}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-6">
            {/* Current Status */}
            <div className="space-y-2">
              <Label>{t('will.fields.currentStatus')}</Label>
              <div className="flex items-center gap-3">
                <Select 
                  value={formData.status} 
                  onValueChange={(value) => setFormData(prev => ({ ...prev, status: value }))}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {statusOptions.map((status) => (
                      <SelectItem key={status.key} value={status.key}>
                        {status.label}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
                <Badge variant={getStatusVariant(formData.status)}>
                  {statusOptions.find(opt => opt.key === formData.status)?.label || formData.status}
                </Badge>
              </div>
              <p className="text-sm text-muted-foreground">
                {t('will.descriptions.statusDescription')}
              </p>
            </div>

            {/* Physical Location */}
            <div className="space-y-2">
              <Label htmlFor="location">{t('will.fields.physicalLocation')}</Label>
              <Input
                id="location"
                placeholder={t('will.placeholders.physicalLocation')}
                value={formData.physical_location}
                onChange={(e) => setFormData(prev => ({ ...prev, physical_location: e.target.value }))}
              />
              <p className="text-sm text-muted-foreground">
                {t('will.descriptions.physicalLocationDescription')}
              </p>
            </div>

            {/* Executor Contact */}
            <div className="space-y-2">
              <Label>{t('will.fields.executorContact')}</Label>
              <Select
                value={formData.executor_contact_id}
                onValueChange={(value) => setFormData(prev => ({ ...prev, executor_contact_id: value }))}
              >
                <SelectTrigger>
                  <SelectValue placeholder={t('will.placeholders.selectContact')} />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="">{t('will.options.noContactSelected')}</SelectItem>
                  {contacts.map((contact) => (
                    <SelectItem key={contact.id} value={contact.id}>
                      <div className="flex items-center gap-2">
                        <User className="h-4 w-4" />
                        <span>{contact.name}</span>
                        <Badge variant="outline" className="text-xs">
                          {contact.role}
                        </Badge>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
              <p className="text-sm text-muted-foreground">
                {t('will.descriptions.executorDescription')}
              </p>
            </div>

            {/* Notes */}
            <div className="space-y-2">
              <Label htmlFor="notes">{t('will.fields.additionalNotes')}</Label>
              <Textarea
                id="notes"
                placeholder={t('will.placeholders.additionalNotes')}
                value={formData.notes}
                onChange={(e) => setFormData(prev => ({ ...prev, notes: e.target.value }))}
                rows={3}
              />
            </div>

            <Button onClick={handleSave} disabled={isSaving} className="w-full">
              {isSaving ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  {t('will.buttons.saving')}
                </>
              ) : (
                <>
                  <Save className="mr-2 h-4 w-4" />
                  {t('will.buttons.saveWillInfo')}
                </>
              )}
            </Button>
          </CardContent>
        </Card>

        {/* Document Upload */}
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Upload className="h-5 w-5" />
              {t('will.sections.willDocument')}
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            {will?.document_path ? (
              <div className="space-y-4">
                <div className="p-4 bg-muted/50 rounded-lg">
                  <div className="flex items-center gap-2 mb-2">
                    <FileSignature className="h-4 w-4 text-primary" />
                    <span className="font-medium">{t('will.document.uploaded')}</span>
                  </div>
                  <p className="text-sm text-muted-foreground">
                    {t('will.document.uploadedDescription')}
                  </p>
                </div>
                <Button
                  variant="outline"
                  onClick={() => setShowUpload(true)}
                  className="w-full"
                >
                  <Upload className="mr-2 h-4 w-4" />
                  {t('will.buttons.uploadNewVersion')}
                </Button>
              </div>
            ) : (
              <div className="space-y-4">
                <div className="text-center py-8">
                  <Upload className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
                  <h3 className="font-medium mb-2">{t('will.document.noDocument')}</h3>
                  <p className="text-sm text-muted-foreground mb-4">
                    {t('will.document.noDocumentDescription')}
                  </p>
                  <Button onClick={() => setShowUpload(true)}>
                    <Upload className="mr-2 h-4 w-4" />
                    {t('will.buttons.uploadDocument')}
                  </Button>
                </div>
              </div>
            )}

            {showUpload && (
              <div className="mt-4">
                <DocumentUpload
                  onDocumentUploaded={handleDocumentUploaded}
                  onCancel={() => setShowUpload(false)}
                />
              </div>
            )}
          </CardContent>
        </Card>
      </div>
    </div>
  );
};

