      console.error('[Pohƒæad str√°≈æcu] Chyba pri naƒç√≠tanie d√°t pre str√°≈æcu:', {
        timestamp,
        operation: 'loadGuardianData',
        errorCode,
        errorMessage,
        errorDetails: error,
        stack: error?.stack
      });

      // Pou≈æ√≠vateƒæsky pr√≠vetiv√° spr√°va
      let userMessage = 'Nastala chyba pri naƒç√≠tanie d√°t pre str√°≈æcu.';

      // ≈†pecifick√© spr√°vy podƒæa typu chyby
      if (error?.code === 'PGRST116') {
        userMessage = 'Po≈æadovan√© d√°ta neboli n√°jden√©.';
      } else if (error?.message?.includes('network')) {
