      console.error('[Pozv√°nka str√°≈æcu] Chyba pri prijatie pozv√°nky:', {
        timestamp,
        operation: 'handleAccept',
        errorCode,
        errorMessage,
        errorDetails: error,
        stack: error?.stack
      });

      // Pou≈æ√≠vateƒæsky pr√≠vetiv√° spr√°va
      let userMessage = 'Nastala chyba pri prijatie pozv√°nky.';

      // ≈†pecifick√© spr√°vy podƒæa typu chyby
      if (error?.code === 'PGRST116') {
        userMessage = 'Po≈æadovan√© d√°ta neboli n√°jden√©.';
      } else if (error?.message?.includes('network')) {
        userMessage = 'Chyba pripojenia. Skontrolujte internetov√© pripojenie.';
      } else if (error?.message?.includes('permission')) {
        userMessage = 'Nem√°te opr√°vnenie na t√∫to akciu.';
      } else if (error?.message?.includes('duplicate')) {
        userMessage = 'Tak√Ωto z√°znam u≈æ existuje.';
      }

      toast.error(userMessage);
    } finally {
      setProcessing(false);
    }
  };

  const handleDecline = async () => {
    if (!invitation) return;

    setProcessing(true);
    try {
      const { error } = await supabase
        .from('guardians')
        .update({
          invitation_status: 'declined'
        })
        .eq('id', invitation.id);

      if (error) {
        throw error;
      }

      toast.success(t('inviteAcceptance.messages.declined'));
      navigate('/');

        } catch (error: Record<string, unknown>) {
      const timestamp = new Date().toISOString();
      const errorMessage = error?.message || 'Nezn√°ma chyba';
      const errorCode = error?.code || 'UNKNOWN_ERROR';

      // Detailn√© logovanie pre debugging
      console.error('[Pozv√°nka str√°≈æcu] Chyba pri odmietnutie pozv√°nky:', {
        timestamp,
        operation: 'handleDecline',
        errorCode,
        errorMessage,
        errorDetails: error,
        stack: error?.stack
      });

      // Pou≈æ√≠vateƒæsky pr√≠vetiv√° spr√°va
      let userMessage = 'Nastala chyba pri odmietnutie pozv√°nky.';

      // ≈†pecifick√© spr√°vy podƒæa typu chyby
      if (error?.code === 'PGRST116') {
        userMessage = 'Po≈æadovan√© d√°ta neboli n√°jden√©.';
      } else if (error?.message?.includes('network')) {
        userMessage = 'Chyba pripojenia. Skontrolujte internetov√© pripojenie.';
      } else if (error?.message?.includes('permission')) {
        userMessage = 'Nem√°te opr√°vnenie na t√∫to akciu.';
      } else if (error?.message?.includes('duplicate')) {
        userMessage = 'Tak√Ωto z√°znam u≈æ existuje.';
      }

      toast.error(userMessage);
    } finally {
      setProcessing(false);
    }
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-pulse">
          <Shield className="h-8 w-8 text-primary mx-auto mb-4" />
